#!/bin/bash; for file in *.html; do if [[ "$file" != "header-template-new.html" && "$file" != "footer-template-new.html" ]]; then sed -i "" -e "s/<header hx-get=\"header-template-new.html\" hx-trigger=\"load\" hx-swap=\"outerHTML\">/<div hx-get=\"header-template-new.html\" hx-trigger=\"load\" hx-swap=\"outerHTML\">/g" -e "s/<\/header>/<\/div>/g" -e "s/<footer hx-get=\"footer-template-new.html\" hx-trigger=\"load\" hx-swap=\"outerHTML\">/<div hx-get=\"footer-template-new.html\" hx-trigger=\"load\" hx-swap=\"outerHTML\">/g" -e "s/<\/footer>/<\/div>/g" "$file"; fi; done
